class SavePhoto extends StatefulWidget {
  SavePhoto() : super();
  @override
  _SavePhotoState createState() => _SavePhotoState();
}
class _SavePhotoState extends State<SavePhoto> {

  Future<File> imageFile;
  Image image;
  DBHelper dbHelper;
  List<Photo> images;

  @override
  void initState() {
    super.initState();
    images = [];
    dbHelper = DBHelper();
    refreshImage();
  }

  refreshImage(){
    dbHelper.getList().then((imgs){
      setState(() {
        images.clear();
        images.addAll(imgs);
      });
    });
  }
  pickImageFormGallery(){
    ImagePicker.pickImage(source: ImageSource.gallery).then((imgFile){
      String imgString = Utility.base64String(imgFile.readAsBytesSync());
      print(imgString);
      Photo photo = Photo(id: 0, photoName: imgString);
      dbHelper.save(photo);
      refreshImage();
    });
  }

  @override
  Widget build(BuildContext context) {
    gridView(){
      return Padding(
          padding: EdgeInsets.all(5.0),
          child: GridView.count(
            crossAxisCount: 2,
            childAspectRatio: 1.0,
            mainAxisSpacing: 4.0,
            crossAxisSpacing: 4.0,
            children: images.map((photo) {
              print(photo);
              return Utility.imageFromBase64String(photo.photoName);
            }).toList(),
          )
      );
    }

    return MaterialApp(
      home: Scaffold(
        appBar: AppBar(
          actions: [
            IconButton(icon: Icon(Icons.add), onPressed: (){pickImageFormGallery();})
          ],
        ),
        body: Center(
          child: Column(
            mainAxisAlignment: MainAxisAlignment.start,
            children: [
              Flexible(child: gridView())
            ],
          ),
        ),
      ),
    );
  }
}

